mapper.update:
update member2
set passwd = #{passwd}		
	where id = #{id}

update member2
set fname = #{fname}		
	where id = #{id}

mapper.select:
SELECT id, mname, tel, email, zipcode, address1, address2,
fname,  r
from(
	SELECT id, mname, tel, email, zipcode, address1, address2,
	fname, rownum r
	from(
		SELECT id, mname, tel, email, zipcode, address1, 
		address2, fname
		FROM member2



				mname like '%'||#{word}||'%'


				id like '%'||#{word}||'%'


				email like '%'||#{word}||'%'



		ORDER BY mdate DESC 
	)

)where r >= #{sno} and r <= #{eno}

select count(*) from member2
where id = #{id}
and passwd = #{passwd}
